import React, { useState, useEffect } from 'react';
import authService from '../services/authService';
import '../../styles/UserRegistration.css';

const UserRegistration = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [fullName, setFullName] = useState('');
  const [signupStatus, setSignupStatus] = useState(null);
  const [isSubmitting, setIsSubmitting] = useState(false);

  const handleSignup = async (e) => {
    e.preventDefault();

    try {
      setIsSubmitting(true);
      await authService.userRegistration(fullName, username, password);
      setSignupStatus('success');
    } catch (error) {
      console.error('Signup failed', error);
      setSignupStatus('failed');
    } finally {
      setIsSubmitting(false);
    }
  };

  useEffect(() => {
    const closeDialog = () => {
      setTimeout(() => setSignupStatus(null), 2000);
    };

    if (signupStatus) {
      closeDialog();
    }
  }, [signupStatus]);

  return (
    <div className="signup-container">
      <h2 className="signup-heading">SIGN UP</h2>
      <form className="signup-form" onSubmit={handleSignup}>
        {/* ... (your input fields) */}
        <button className="signup-button" type="submit" disabled={isSubmitting}>
          {isSubmitting ? 'Signing Up...' : 'Sign Up'}
        </button>

        {signupStatus && (
          <div className={`dialog ${signupStatus === 'success' ? 'success' : 'error'}`}>
            {signupStatus === 'success' ? 'Signup successful!' : 'Signup failed. Please try again.'}
          </div>
        )}
      </form>
    </div>
  );
};

export default UserRegistration;
